package com.info498.bestgroup.tindar;

import android.content.Context;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.graphics.Canvas;
import android.graphics.Color;
import android.graphics.Paint;
import android.util.Base64;
import android.util.Log;
import android.view.View;

/**
 * Created by iguest on 6/8/15.
 */
public class CanvasView extends View {

    Bitmap drawing;

    public CanvasView(Context context, String encodedString) {
        super(context);

        this.drawing = StringToBitMap(encodedString);
       // String test = "";
       // Log.i("bitmap", encodedString);
        //Log.i("bitmapTest", test);
        //this.drawing = StringToBitMap(test);
       // this.drawing = StringToBitMap(encodedString);
    }

    public Bitmap StringToBitMap(String encodedString){
        try {
            byte [] encodeByte= Base64.decode(encodedString, Base64.DEFAULT);

            Bitmap bitmap= BitmapFactory.decodeByteArray(encodeByte, 0, encodeByte.length);

            //drawing = bitmap;
            return bitmap;
        } catch(Exception e) {
            e.getMessage();
            return null;
        }
    }
    @Override
    public void onDraw (Canvas canvas){
        super.onDraw(canvas);
        canvas.drawColor(Color.WHITE);
        try {
            Bitmap temp = Bitmap.createScaledBitmap(drawing,canvas.getWidth(), canvas.getHeight(), false);
            canvas.drawBitmap(temp, 0, 0, null);
        } catch(Exception e){
            //sometimes the images are the wrong size
            Log.e("Canvas","failed to draw");
            e.printStackTrace();
        }
    }
}